
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE PROCEDURE [dbo].[InquiryStatusUpsert]    @Id int,	@InquiryStatusName nchar(50),	@IsActive bitASBEGIN	SET NOCOUNT ON; IF @Id = 0    BEGIN         -- Check if the BrandName already exists        IF NOT EXISTS (SELECT * FROM InquiryStatuses WHERE InquiryStatusName = @InquiryStatusName)        BEGIN            INSERT INTO InquiryStatuses(InquiryStatusName,IsActive)            VALUES(@InquiryStatusName,@IsActive)        END        ELSE        BEGIN            -- Handle the case when the BrandName already exists            RAISERROR('InquiryStatuses with the same name already exists.', 16, 1);        END    END    ELSE    BEGIN        -- Check if the BrandName already exists for a different brand (when performing an update)        IF NOT EXISTS (SELECT * FROM InquiryStatuses WHERE InquiryStatusName = @InquiryStatusName AND Id <> @Id)        BEGIN            UPDATE InquiryStatuses            SET                InquiryStatusName = @InquiryStatusName,                IsActive = @IsActive            WHERE Id = @Id        END        ELSE        BEGIN            -- Handle the case when the BrandName already exists for a different brand            RAISERROR('InquiryStatuses with the same name already exists for a different brand.', 16, 1);        END    ENDEND
